#!/usr/bin/env python3.4# -*-coding:utf-8-*-#Ce n'est encore qu'un testimport osfrom tkinter import *def multiplier_par_3(nbAMultiplier=0):    nbAMultiplier = nbAMultiplier * 3    return nbAMultiplierif __name__ == "__main__":    #Création d'une fenêtre    fenetre = Tk();    fenetre.title('Ma fenetre')    fenetre['bg']='bisque' #Couleur de fond    #Création de Frames (conteneurs)    Frame001 = Frame(fenetre, width = 100, height = 100, borderwidth = 1)    Frame001.pack(side = LEFT, padx=10, pady=10)        Frame002 = Frame(fenetre, borderwidth = 2, relief=GROOVE)    Frame002.pack(side = LEFT, padx=10, pady=10)    # création d'un widget Frame dans un widget Frame    # le widget Frame1 est le parent du widget Frame3    # le parent du widget Frame1 est le widget Mafenetre (fenêtre principale)        Frame003 = Frame(Frame001,bg="white",borderwidth=2,relief=GROOVE)#relief = type de bord    Frame003.pack(side=LEFT,padx=10,pady=10)    # création d'un widget Label et d'un widget Button dans un widget Frame    Label(Frame001,text="RDV dentiste samedi à 15h").pack(padx=10,pady=10)    Button(Frame001,text="Effacer",fg='navy', bg='red',command=Frame001.destroy).pack(padx=10,pady=10)    Label(Frame002,text="Réviser le contrôle d'info").pack(padx=10,pady=10)    Button(Frame002,text="Effacer",fg='navy',command=Frame002.destroy).pack(padx=10,pady=10)    Label(Frame003,text="RDV dentiste à 10h",bg="white").pack(padx=10,pady=10)    Button(Frame003,text="Effacer",fg='navy',command=Frame003.destroy).pack(padx=10,pady=10)       #####################################################################        #Boucle Tkinter, active jusqu'à la fermeture de la fenêtre    fenetre.mainloop()